import { Button, Link } from "@mui/material";
import { useEffect } from "react";
import Caver from "caver-js";

const MainPage = () => {
  const caver = new Caver(window.klaytn);
  useEffect(() => {
    window.klaytn.enable();
  }, []);

  const signTransaction = () => {
    caver.klay
      .sendTransaction({
        type: "VALUE_TRANSFER",
        from: window.klaytn.selectedAddress,
        to: "0xda885688cffbe34536482696ab9aac93fa330596",
        value: caver.utils.toPeb("1", "peb"),
        gas: 800000,
      })
      .once("transactionHash", (transactionHash) => {
        console.log("txHash", transactionHash);
      })
      .once("receipt", (receipt) => {
        console.log("receipt", receipt);
      })
      .once("error", (error) => {
        console.log("error", error);
      });
  };

  const signTransactionSmartContractExe = async () => {
    const data = caver.klay.abi.encodeFunctionCall(
      {
        name: "setIpfsAddress",
        type: "function",
        inputs: [
          {
            type: "string",
            name: "date",
          },
          {
            type: "string",
            name: "ipfs",
          },
        ],
      },
      ["helloworld!", "what?"]
    );

    caver.klay.sendTransaction({
      type: "SMART_CONTRACT_EXECUTION",
      from: window.klaytn.selectedAddress,
      to: "0x469f65e038A41fC36eCfBf0C366131bF87892388",
      gas: "8000000",
      data,
    });
  };

  const checkResult = async () => {
    const vari = await IPFSCONTRACT.methods
      .getIpfsAddress("helloworld!")
      .call();
    console.log(vari);
  };
  return (
    <>
      <Link href="/makeNFT">Skkryptoken 만들기</Link>
      <br />
      <Link href="/Search">SkkrypToken 조회하기</Link>
      <br />
      <Button onClick={signTransaction}>
        value transfer 트랜잭션 서명하기
      </Button>
      <br />
      <Button onClick={signTransactionSmartContractExe}>
        스마트 컨트랙트 트랜잭션 서명하기
      </Button>
    </>
  );
};

export default MainPage;
